    <div>
      <div className="header">
        <h1>Todos List</h1>
      </div>
      <form>
      <input type="text" placeholder="Enter a Todo..." className="task-input" value={input} onChange={onInputChange} />
       <button className="button-add" type="submit">Add</button>
      </form>

    </div>
  );
};

export default Form;
