  const [todos, setTodos] = useState([]);
  const [EditTodo, setEditTodo] = useState("")
  return (
    <div className="container">
      <div className="app-wrapper ">
        <Form input={input}
          setInput={setinput}
          todos={todos}
          setTodos={setTodos} />
        <TodoList todos={todos} setTodos ={setTodos}/>
      </div>
    </div>
  );
}

export default App;
